        return _whitelistedAllowToMint[_address];
    }


    function changeZookeeperDropId(uint256 id) public onlyOwner {
        zooKeeperDropId = id;
    }


    function changeMaxEggForSublime(uint256 max) public onlyOwner {
        maxEggForSublime = max;
