// SPDX-License-Identifier: MIT

pragma solidity >=0.8.4;

import { IMedia } from "./IMedia.sol";
import { IMarket } from "./IMarket.sol";

interface IZoo {


  // Declare an Event
  event AddDrop(address indexed dropAddress, string title, uint256 eggSupply);
  event BreedAnimal(address indexed from, uint256 parentA, uint256 parentB, uint256 indexed eggID);
  event Burn(address indexed from, uint256 indexed tokenID);
  event BuyEgg(address indexed from, uint256 indexed eggID);
  event Free(address indexed from, uint256 indexed tokenID, uint256 indexed yields);
  event Hatch(address indexed from, uint256 eggID, uint256 indexed tokenID);
  event Mint(address indexed from, uint256 indexed tokenID);
  event Swap(address indexed owner, uint256 indexed tokenID, uint256 indexed chainId);

    enum Type {
        BASE_EGG,
        BASE_ANIMAL,
        HYBRID_EGG,
        HYBRID_ANIMAL
    }

    enum AdultHood {
        BABY,
        TEEN,
        ADULT
    }

    struct Rarity {
        string  name;
        uint256 probability;
        uint256 yields;
        uint256 boost;
    }

    struct Breed {
        uint256 count;
        uint256 timestamp;
    }

    struct Parents {
        string  animalA;
        string  animalB;
        uint256 tokenA;
        uint256 tokenB;
    }

    struct Meta {
        uint256 eggID;       // originating egg
        uint256 dropID;      // originating drop
        bool burned;         // token has been burned
        bool swapped;        // token has been swapped
    }

    struct Birth {
        Parents parents;
        uint40 timestamp;    // time created
        uint40 birthday;     // birth block
    }


    struct URIs {
        IMedia.MediaData dataBaby;
        IMedia.MediaData dataTeen;
        IMedia.MediaData dataAdult;
    }

    struct YieldsBoost{
        uint256 yields;
        uint256 boost;
    }

    struct StageYields{
        YieldsBoost baby;
        YieldsBoost teen;
        YieldsBoost adult;
    }

    struct Token {
        Rarity  rarity;
        Type  kind;
        string  name;
        uint256 id;           // unique ID
        string  customName;   // optional, paid feature
        Breed   breed;
        Meta meta;
        IMedia.MediaData data;
        Birth birthValues;
        IMarket.BidShares bidShares;
        uint256 dropEgg;
        AdultHood stage;
    }
}

src/Market.sol.content:
// SPDX-License-Identifier: GPL-3.0
// Forked from https://github.com/ourzora/core @ 450cd154bfbb70f62e94050cc3f1560d58e0506a

pragma solidity >=0.8.4;
pragma experimental ABIEncoderV2;

import { SafeMath } from '@openzeppelin/contracts/utils/math/SafeMath.sol';
import { IERC721 } from '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import { IERC20 } from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import { SafeERC20 } from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import { Ownable } from '@openzeppelin/contracts/access/Ownable.sol';
import { Decimal } from './Decimal.sol';
import { Media } from './Media.sol';
import { IMarket } from './interfaces/IMarket.sol';
import { IDrop } from './interfaces/IDrop.sol';
import { IZoo } from './interfaces/IZoo.sol';

import './console.sol';

/**
 * @title A Market for pieces of media
 * @notice This contract contains all of the market logic for Media
 */
contract Market is IMarket, Ownable {
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  /* *******
   * Globals
   * *******
   */
  // Address of the media contract that can call this market
  address public mediaContract;

  // Deployment Address
  address private _owner;

  // Mapping from token to mapping from bidder to bid
  mapping(uint256 => mapping(address => Bid)) private _tokenBidders;

  // Mapping from token type name to mapping from bidder to bid
  mapping(string => mapping(address => Bid)) private _lazyTokenBidders;

  // Mapping from token to the bid shares for the token
  mapping(uint256 => BidShares) private _bidShares;

  // Mapping from token to the current ask for the token
  mapping(uint256 => Ask) private _tokenAsks;

  // Mapping of bidders that are authorized to place offline bids
  mapping(address => bool) private _offlineBidders;

  /* *********
   * Modifiers
   * *********
   */

  /**
   * @notice require that the msg.sender is the configured media contract
   */
  modifier onlyMediaCaller() {
    require(mediaContract == msg.sender, 'Market: Only media contract');
    _;
  }

  /* ****************
   * View Functions
   * ****************
   */
  function bidForTokenBidder(uint256 tokenId, address bidder) external view override returns (Bid memory) {
    return _tokenBidders[tokenId][bidder];
  }

  function lazyBidForTokenBidder(uint256 dropId, string memory name, address bidder) external view override returns (Bid memory) {
    string memory dropEggName = getDropEggName(dropId, name);
    return _lazyTokenBidders[dropEggName][bidder];
  }

  function currentAskForToken(uint256 tokenId) external view override returns (Ask memory) {
    return _tokenAsks[tokenId];
  }

  function bidSharesForToken(uint256 tokenId) public view override returns (BidShares memory) {
    return _bidShares[tokenId];
  }

  function isOfflineBidder(address bidder) public view override returns (bool) {
    return _offlineBidders[bidder];
  }

  function setOfflineBidder(address bidder, bool authorized) external override onlyOwner {
    _offlineBidders[bidder] = authorized;
  }

  /**
   * @notice Validates that the bid is valid by ensuring that the bid amount can be split perfectly into all the bid shares.
   *  We do this by comparing the sum of the individual share values with the amount and ensuring they are equal. Because
   *  the splitShare function uses integer division, any inconsistencies with the original and split sums would be due to
   *  a bid splitting that does not perfectly divide the bid amount.
   */
  function isValidBid(uint256 tokenId, uint256 bidAmount) public view override returns (bool) {
    BidShares memory bidShares = bidSharesForToken(tokenId);
    require(isValidBidShares(bidShares), 'Market: Invalid bid shares for token');
    return bidAmount != 0 && (bidAmount == splitShare(bidShares.creator, bidAmount).add(splitShare(bidShares.prevOwner, bidAmount)).add(splitShare(bidShares.owner, bidAmount)));
  }

  function isValidLazyBid(IDrop.Egg memory egg, uint256 bidAmount) public pure returns (bool) {
    BidShares memory bidShares = egg.bidShares;
    require(isValidBidShares(bidShares), 'Market: Invalid lazy bid shares for token');
    return bidAmount != 0 && (bidAmount == splitShare(bidShares.creator, bidAmount).add(splitShare(bidShares.prevOwner, bidAmount)).add(splitShare(bidShares.owner, bidAmount)));
  }

  /**
   * @notice Validates that the provided bid shares sum to 100
   */
  function isValidBidShares(BidShares memory bidShares) public pure override returns (bool) {
    return bidShares.creator.value.add(bidShares.owner.value).add(bidShares.prevOwner.value) == uint256(100).mul(Decimal.BASE);
  }

  /**
   * @notice return a % of the specified amount. This function is used to split a bid into shares
   * for a media's shareholders.
   */
  function splitShare(Decimal.D256 memory sharePercentage, uint256 amount) public pure override returns (uint256) {
    return Decimal.mul(amount, sharePercentage).div(100);
  }

  /**
   * @notice Sets the media contract address. This address is the only permitted address that
   * can call the mutable functions. This method can only be called once.
   */
  function configure(address mediaContractAddress) external override onlyOwner {
    require(mediaContractAddress != address(0), 'Market: cannot set media contract as zero address');

    mediaContract = mediaContractAddress;
  }

  /**
   * @notice Sets bid shares for a particular tokenId. These bid shares must
   * sum to 100.
   */
  function setBidShares(uint256 tokenId, BidShares memory bidShares) public override onlyMediaCaller {
    require(isValidBidShares(bidShares), 'Market: Invalid bid shares, must sum to 100');
    _bidShares[tokenId] = bidShares;
    emit BidShareUpdated(tokenId, bidShares);
  }

  /**
   * @notice Sets the ask on a particular media. If the ask cannot be evenly split into the media's
   * bid shares, this reverts.
   */
  function setAsk(uint256 tokenId, Ask memory ask) public override onlyMediaCaller {
    require(isValidBid(tokenId, ask.amount), 'Market: Ask invalid for share splitting');

    _tokenAsks[tokenId] = ask;
    emit AskCreated(tokenId, ask);
  }

  /**
   * @notice removes an ask for a token and emits an AskRemoved event
   */
  function removeAsk(uint256 tokenId) external override onlyMediaCaller {
    emit AskRemoved(tokenId, _tokenAsks[tokenId]);
    delete _tokenAsks[tokenId];
  }

  /**
   * @notice Removes the bid on a particular media for a bidder. The bid amount
   * is transferred from this contract to the bidder, if they have a bid placed.
   */
  function removeBid(uint256 tokenId, address bidder) public override onlyMediaCaller {
    Bid storage bid = _tokenBidders[tokenId][bidder];
    address bidCurrency = bid.currency;
    uint256 bidAmount = bid.amount;
    bool bidOffline = bid.offline;

    require(bid.amount > 0, 'Market: cannot remove bid amount of 0');

    emit BidRemoved(tokenId, bid);
    delete _tokenBidders[tokenId][bidder];

    console.log('Market.removeBid', tokenId, bidCurrency, bidOffline);

    if (bidCurrency != address(0) && !bidOffline) {
      IERC20 token = IERC20(bidCurrency);
      token.safeTransfer(bidder, bidAmount);
    }
  }

  function setBid(
    uint256 tokenId,
    Bid memory bid,
    address spender
  ) public override onlyMediaCaller {
    BidShares memory bidShares = _bidShares[tokenId];
    require(bidShares.creator.value.add(bid.sellOnShare.value) <= uint256(100).mul(Decimal.BASE), 'Market: Sell on fee invalid for share splitting');
    require(bid.bidder != address(0), 'Market: bidder cannot be 0 address');
    require(!bid.offline || (bid.offline && isOfflineBidder(bid.bidder)), 'Market: Only whitelisted offline bidder');
    require(bid.amount != 0, 'Market: cannot bid amount of 0');
    require(bid.currency != address(0), 'Market: bid currency cannot be 0 address');
    require(bid.recipient != address(0), 'Market: bid recipient cannot be 0 address');

    Bid storage existingBid = _tokenBidders[tokenId][bid.bidder];

    // If there is an existing bid, refund it before continuing
    if (existingBid.amount > 0) {
      removeBid(tokenId, bid.bidder);
    }

    uint256 bidAmount = bid.amount;

    if (bid.currency != address(0) && !bid.offline) {
      IERC20 token = IERC20(bid.currency);
      // We must check the balance that was actually transferred to the market,
      // as some tokens impose a transfer fee and would not actually transfer the
      // full amount to the market, resulting in locked funds for refunds & bid acceptance
      uint256 beforeBalance = token.balanceOf(address(this));
      token.safeTransferFrom(spender, address(this), bid.amount);
      uint256 afterBalance = token.balanceOf(address(this));
      bidAmount = afterBalance.sub(beforeBalance);
    }

    _tokenBidders[tokenId][bid.bidder] = Bid(bidAmount, bid.currency, bid.bidder, bid.recipient, bid.sellOnShare, bid.offline);

    emit BidCreated(tokenId, bid);

    // DO NOT automatically accept bids
    // if (_tokenAsks[tokenId].currency != address(0) && bid.currency == _tokenAsks[tokenId].currency && bid.amount >= _tokenAsks[tokenId].amount) {
    //   // Finalize exchange
    //   _finalizeNFTTransfer(tokenId, bid.bidder);
    // }
  }

  /**
   * @notice Sets the bid on a particular media for a bidder. The token being used to bid
   * is transferred from the spender to this contract to be held until removed or accepted.
   * If another bid already exists for the bidder, it is refunded.
   */
  function setLazyBidFromApp(
    uint256 dropId,
    IDrop.Egg memory egg,
    Bid memory bid,
    address spender
  ) external override onlyMediaCaller {
    require(egg.bidShares.creator.value.add(bid.sellOnShare.value) <= uint256(100).mul(Decimal.BASE), 'Market: Sell on fee invalid for share splitting');
    require(bid.bidder != address(0), 'Market: bidder cannot be 0 address');
    require(!bid.offline || (bid.offline && isOfflineBidder(bid.bidder)), 'Market: Only whitelisted offline bidder');
    require(bid.amount != 0, 'Market: cannot bid amount of 0');
    // require(bid.currency != address(0), 'Market: bid currency cannot be 0 address');
    require(bid.recipient != address(0), 'Market: bid recipient cannot be 0 address');

    uint256 bidAmount = bid.amount;

    if (bid.currency != address(0) && !bid.offline) {
      IERC20 token = IERC20(bid.currency);
      // We must check the balance that was actually transferred to the market,
      // as some tokens impose a transfer fee and would not actually transfer the
      // full amount to the market, resulting in locked funds for refunds & bid acceptance
      uint256 beforeBalance = token.balanceOf(address(this));
      token.safeTransferFrom(spender, address(this), bid.amount);
      uint256 afterBalance = token.balanceOf(address(this));
      bidAmount = afterBalance.sub(beforeBalance);
    }

    string memory dropEggName = getDropEggName(dropId, egg.name);

    _lazyTokenBidders[dropEggName][bid.bidder] = Bid(bidAmount, bid.currency, bid.bidder, bid.recipient, bid.sellOnShare, bid.offline);

    emit LazyBidCreated(dropId, egg.name, bid); 
  }

  function getDropEggName(uint256 tokenId, string memory name) internal pure returns(string memory) {
    return string(abi.encodePacked(tokenId, '-', name));
  }

  /**
   * @notice Removes the bid on a particular media for a bidder. The bid amount
   * is transferred from this contract to the bidder, if they have a bid placed.
   */
  function removeLazyBidFromApp(uint256 dropId, string memory name, address bidder) public override onlyMediaCaller {
    string memory dropEggName = getDropEggName(dropId, name);
    Bid storage bid = _lazyTokenBidders[dropEggName][bidder];
    address bidCurrency = bid.currency;
    uint256 bidAmount = bid.amount;
    bool bidOffline = bid.offline;

    require(bid.amount > 0, 'Market: cannot remove bid amount of 0');

    emit LazyBidRemoved(dropId, name, bid);
    delete _lazyTokenBidders[dropEggName][bidder];

    console.log('Market.removeLazyBidFromApp', dropEggName, bidCurrency, bidOffline);

    if (bidCurrency != address(0) && !bidOffline) {
      IERC20 token = IERC20(bidCurrency);
      token.safeTransfer(bidder, bidAmount);
    }
  }

  function acceptLazyBidFromApp(uint256 dropId, IDrop.Egg memory egg, IZoo.Token memory token, Bid calldata expectedBid) external override onlyMediaCaller {
    string memory dropEggName = getDropEggName(dropId, egg.name);
    Bid memory bid = _lazyTokenBidders[dropEggName][expectedBid.bidder];
    require(bid.amount > 0, 'Market: cannot accept bid of 0');
    require(
      bid.amount == expectedBid.amount && bid.currency == expectedBid.currency && bid.sellOnShare.value == expectedBid.sellOnShare.value && bid.recipient == expectedBid.recipient,
      'Market: Unexpected bid found.'
    );
    require(isValidLazyBid(egg, bid.amount), 'Market: Bid invalid for share splitting');

    _finalizeLazyMint(dropId, egg, token, bid.bidder);    
  }

  /**
   * @notice Given a token ID and a bidder, this method transfers the value of
   * the bid to the shareholders. It also transfers the ownership of the media
   * to the bid recipient. Finally, it removes the accepted bid and the current ask.
   */
  function _finalizeNFTTransfer(uint256 tokenId, address bidder) private {
    Bid memory bid = _tokenBidders[tokenId][bidder];
    BidShares storage bidShares = _bidShares[tokenId];

    if (bid.currency != address(0) && !bid.offline) {
      IERC20 token = IERC20(bid.currency);

      // Transfer bid share to owner of media
      token.safeTransfer(IERC721(mediaContract).ownerOf(tokenId), splitShare(bidShares.owner, bid.amount));
      // Transfer bid share to creator of media
      token.safeTransfer(owner(), splitShare(bidShares.creator, bid.amount));
      // Transfer bid share to previous owner of media (if applicable)
      token.safeTransfer(Media(mediaContract).previousTokenOwners(tokenId), splitShare(bidShares.prevOwner, bid.amount));
    }

    // Transfer media to bid recipient
    Media(mediaContract).auctionTransfer(tokenId, bid.recipient);

    // Calculate the bid share for the new owner,
    // equal to 100 - creatorShare - sellOnShare
    bidShares.owner = Decimal.D256(uint256(100).mul(Decimal.BASE).sub(_bidShares[tokenId].creator.value).sub(bid.sellOnShare.value));
    // Set the previous owner share to the accepted bid's sell-on fee
    bidShares.prevOwner = bid.sellOnShare;

    // Remove the accepted bid
    delete _tokenBidders[tokenId][bidder];

    emit BidShareUpdated(tokenId, bidShares);
    emit BidFinalized(tokenId, bid);
  }

  /**
   * @notice Given a token ID and a bidder, this method transfers the value of
   * the bid to the shareholders. It also transfers the ownership of the media
   * to the bid recipient. Finally, it removes the accepted bid and the current ask.
   */
  function _finalizeLazyMint(uint256 dropId, IDrop.Egg memory egg, IZoo.Token memory token, address bidder) private {

    string memory dropEggName = getDropEggName(dropId, egg.name);

    Bid memory bid = _lazyTokenBidders[dropEggName][bidder];

    BidShares memory bidShares = egg.bidShares;

    if (bid.currency != address(0) && !bid.offline) {
      IERC20 erc20Token = IERC20(bid.currency);

      erc20Token.safeTransfer(owner(), bid.amount); // Transfer 100%
    }

    // Transfer media to bid recipient
    Media(mediaContract).mintToken(bid.recipient, token);

    // Calculate the bid share for the new owner,
    // equal to 100 - creatorShare - sellOnShare
    bidShares.owner = Decimal.D256(uint256(100).mul(Decimal.BASE).sub(bidShares.creator.value).sub(bid.sellOnShare.value));
    // Set the previous owner share to the accepted bid's sell-on fee
    bidShares.prevOwner = bid.sellOnShare;

    // Remove the accepted bid
    delete _lazyTokenBidders[dropEggName][bidder];

    emit BidShareUpdated(token.id, bidShares);
    emit LazyBidFinalized(dropId, egg.name, token.id, bid);
  }

  /**
   * @notice Accepts a bid from a particular bidder. Can only be called by the media contract.
   * See {_finalizeNFTTransfer}
   * Provided bid must match a bid in storage. This is to prevent a race condition
   * where a bid may change while the acceptBid call is in transit.
   * A bid cannot be accepted if it cannot be split equally into its shareholders.
   * This should only revert in rare instances (example, a low bid with a zero-decimal token),
   * but is necessary to ensure fairness to all shareholders.
   */
  function acceptBid(uint256 tokenId, Bid calldata expectedBid) external override onlyMediaCaller {
    Bid memory bid = _tokenBidders[tokenId][expectedBid.bidder];
    require(bid.amount > 0, 'Market: cannot accept bid of 0');
    require(
      bid.amount == expectedBid.amount && bid.currency == expectedBid.currency && bid.sellOnShare.value == expectedBid.sellOnShare.value && bid.recipient == expectedBid.recipient,
      'Market: Unexpected bid found.'
    );
    require(isValidBid(tokenId, bid.amount), 'Market: Bid invalid for share splitting');

    _finalizeNFTTransfer(tokenId, bid.bidder);
  }

}

src/Math.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity >=0.8.4;
pragma experimental ABIEncoderV2;

import { SafeMath } from "@openzeppelin/contracts/utils/math/SafeMath.sol";

/// @title Math
/// Library for non-standard Math functions
/// NOTE: This file is a clone of the dydx protocol's Decimal.sol contract.
/// It was forked from https://github.com/dydxprotocol/solo at commit
/// 2d8454e02702fe5bc455b848556660629c3cad36. It has not been modified other than to use a
/// newer solidity in the pragma to match the rest of the contract suite of this project.
library Math {
    using SafeMath for uint256;

    // ============ Library Functions ============

    /*
     * Return target * (numerator / denominator).
     */
    function getPartial(
        uint256 target,
        uint256 numerator,
        uint256 denominator
    ) internal pure returns (uint256) {
        return target.mul(numerator).div(denominator);
    }

    /*
     * Return target * (numerator / denominator), but rounded up.
     */
    function getPartialRoundUp(
        uint256 target,
        uint256 numerator,
        uint256 denominator
    ) internal pure returns (uint256) {
        if (target == 0 || numerator == 0) {
            // SafeMath will check for zero denominator
            return SafeMath.div(0, denominator);
        }
        return target.mul(numerator).sub(1).div(denominator).add(1);
    }

    function to128(uint256 number) internal pure returns (uint128) {
        uint128 result = uint128(number);
        require(result == number, "Math: Unsafe cast to uint128");
        return result;
    }

    function to96(uint256 number) internal pure returns (uint96) {
        uint96 result = uint96(number);
        require(result == number, "Math: Unsafe cast to uint96");
        return result;
    }

    function to32(uint256 number) internal pure returns (uint32) {
        uint32 result = uint32(number);
        require(result == number, "Math: Unsafe cast to uint32");
        return result;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }

    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a > b ? a : b;
    }
}

src/Media.sol.content:
// SPDX-License-Identifier: GPL-3.0
// Forked from https://github.com/ourzora/core @ 450cd154bfbb70f62e94050cc3f1560d58e0506a

pragma solidity >=0.8.4;
pragma experimental ABIEncoderV2;

import { ERC721Burnable } from './ERC721Burnable.sol';
import { ERC721 } from './ERC721.sol';
import { EnumerableSet } from '@openzeppelin/contracts/utils/structs/EnumerableSet.sol';
import { Counters } from '@openzeppelin/contracts/utils/Counters.sol';
import { SafeMath } from '@openzeppelin/contracts/utils/math/SafeMath.sol';
import { Math } from '@openzeppelin/contracts/utils/math/Math.sol';
import { IERC20 } from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import { ReentrancyGuard } from '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import { Ownable } from '@openzeppelin/contracts/access/Ownable.sol';
import { Decimal } from './Decimal.sol';
import { IMarket } from './interfaces/IMarket.sol';
import './interfaces/IMedia.sol';
import './interfaces/IDrop.sol';

import './console.sol';

/**
 * @title A media value system, with perpetual equity to creators
 * @notice This contract provides an interface to mint media with a market
 * owned by the creator.
 */
contract Media is IMedia, ERC721Burnable, ReentrancyGuard, Ownable {
  using Counters for Counters.Counter;
  using SafeMath for uint256;
  using EnumerableSet for EnumerableSet.UintSet;

  /* *******
   * Globals
   * *******
   */

  // Address for the market
  address public marketContract;

    // Address for the app
  address public appContract;

  // Mapping from token to previous owner of the token
  mapping(uint256 => address) public previousTokenOwners;

  // Mapping from token id to creator address
  mapping(uint256 => address) public tokenCreators;

  // Mapping from creator address to their (enumerable) set of created tokens
  mapping(address => EnumerableSet.UintSet) private _creatorTokens;

  // Mapping from token id to sha256 hash of content
  mapping(uint256 => bytes32) public tokenContentHashes;

  // Mapping from token id to sha256 hash of metadata
  mapping(uint256 => bytes32) public tokenMetadataHashes;

  // Mapping from token id to metadataURI
  mapping(uint256 => string) private _tokenMetadataURIs;

  // Mapping from contentHash to bool
  mapping(bytes32 => bool) private _contentHashes;

  //keccak256("Permit(address spender,uint256 tokenId,uint256 nonce,uint256 deadline)");
  bytes32 public constant PERMIT_TYPEHASH = 0x49ecf333e5b8c95c40fdafc95c1ad136e8914a8fb55e9dc8bb01eaa83a2df9ad;

  //keccak256("MintWithSig(bytes32 contentHash,bytes32 metadataHash,uint256 creatorShare,uint256 nonce,uint256 deadline)");
  bytes32 public constant MINT_WITH_SIG_TYPEHASH = 0x2952e482b8e2b192305f87374d7af45dc2eafafe4f50d26a0c02e90f2fdbe14b;

  // Mapping from address to token id to permit nonce
  mapping(address => mapping(uint256 => uint256)) public permitNonces;

  // Mapping from address to mint with sig nonce
  mapping(address => uint256) public mintWithSigNonces;

  /*
   *     bytes4(keccak256('name()')) == 0x06fdde03
   *     bytes4(keccak256('symbol()')) == 0x95d89b41
   *     bytes4(keccak256('tokenURI(uint256)')) == 0xc87b56dd
   *     bytes4(keccak256('tokenMetadataURI(uint256)')) == 0x157c3df9
   *
   *     => 0x06fdde03 ^ 0x95d89b41 ^ 0xc87b56dd ^ 0x157c3df9 == 0x4e222e66
   */
  bytes4 private constant _INTERFACE_ID_ERC721_METADATA = 0x4e222e66;

  Counters.Counter private _tokenIdTracker;

  /* *********
   * Modifiers
   * *********
   */

  /**
   * @notice Require that the token has not been burned and has been minted
   */
  modifier onlyExistingToken(uint256 tokenId) {
    require(_exists(tokenId), 'Media: nonexistent token');
    _;
  }

  /**
   * @notice Require that the token has had a content hash set
   */
  modifier onlyTokenWithContentHash(uint256 tokenId) {
    require(tokenContentHashes[tokenId] != 0, 'Media: token does not have hash of created content');
    _;
  }

  /**
   * @notice Require that the token has had a metadata hash set
   */
  modifier onlyTokenWithMetadataHash(uint256 tokenId) {
    require(tokenMetadataHashes[tokenId] != 0, 'Media: token does not have hash of its metadata');
    _;
  }

  /**
   * @notice Ensure that the provided spender is the approved or the owner of
   * the media for the specified tokenId
   */
  modifier onlyApprovedOrOwner(address spender, uint256 tokenId) {
    require(_isApprovedOrOwner(spender, tokenId), 'Media: Only approved or owner');
    _;
  }

  /**
   * @notice Ensure the token has been created (even if it has been burned)
   */
  modifier onlyTokenCreated(uint256 tokenId) {
    require(_tokenIdTracker.current() > tokenId, 'Media: token with that id does not exist');
    _;
  }

  /**
   * @notice Ensure that the provided URI is not empty
   */
  modifier onlyValidURI(string memory uri) {
    require(bytes(uri).length != 0, 'Media: specified uri must be non-empty');
    _;
  }

  /**
   * @notice require that the msg.sender is the configured app, market or contract owner
   */
  modifier onlyAuthorizedCaller() {
    require(appContract == msg.sender || marketContract == msg.sender || owner() == msg.sender, 'Media: Only app contract, market contract or owner');
    _;
  }

  /**
   * @notice On deployment, set the market contract address and register the
   * ERC721 metadata interface
   */
  constructor(string memory name, string memory symbol) ERC721(name, symbol) {
    _registerInterface(_INTERFACE_ID_ERC721_METADATA);
  }

  /**
   * @notice Sets the market contract address. This address is the only permitted address that
   * can call the mutable functions.
   */
  function configure(address appContractAddr, address marketContractAddr) external onlyOwner {
    require(appContractAddr != address(0), 'Media: cannot set app contract as zero address');
    appContract = appContractAddr;
    require(marketContractAddr != address(0), 'Media: cannot set market contract as zero address');
    marketContract = marketContractAddr;
  }

  /* **************
   * View Functions
   * **************
   */

  /**
   * @notice return the URI for a particular piece of media with the specified tokenId
   * @dev This function is an override of the base OZ implementation because we
   * will return the tokenURI even if the media has been burned. In addition, this
   * protocol does not support a base URI, so relevant conditionals are removed.
   * @return the URI for a token
   */
  function tokenURI(uint256 tokenId) public view override onlyTokenCreated(tokenId) returns (string memory) {
    string memory _tokenURI = _tokenURIs[tokenId];

    return _tokenURI;
  }

  /**
   * @notice Return the metadata URI for a piece of media given the token URI
   * @return the metadata URI for the token
   */
  function tokenMetadataURI(uint256 tokenId) external view override onlyTokenCreated(tokenId) returns (string memory) {
    return _tokenMetadataURIs[tokenId];
  }

  /* ****************
   * Public Functions
   * ****************
   */

  /**
   * @notice see IMedia
   */
  function mint(MediaData memory data, IMarket.BidShares memory bidShares) public override nonReentrant {
    _mintForCreator(msg.sender, data, bidShares);
  }

  /**
   * @notice see IMedia
   */
  function mintWithSig(
    address creator,
    MediaData memory data,
    IMarket.BidShares memory bidShares,
    EIP712Signature memory sig
  ) public override nonReentrant {
    require(sig.deadline == 0 || sig.deadline >= block.timestamp, 'Media: mintWithSig expired');

    bytes32 domainSeparator = _calculateDomainSeparator();

    bytes32 digest = keccak256(
      abi.encodePacked(
        '\x19\x01',
        domainSeparator,
        keccak256(abi.encode(MINT_WITH_SIG_TYPEHASH, data.contentHash, data.metadataHash, bidShares.creator.value, mintWithSigNonces[creator]++, sig.deadline))
      )
    );

    address recoveredAddress = ecrecover(digest, sig.v, sig.r, sig.s);

    require(recoveredAddress != address(0) && creator == recoveredAddress, 'Media: Signature invalid');

    _mintForCreator(recoveredAddress, data, bidShares);
  }

  /**
   * @notice see IMedia
   */
  function getRecentToken(address creator) public view returns (uint256) {
    uint256 length = EnumerableSet.length(_creatorTokens[creator]) - 1;

    return EnumerableSet.at(_creatorTokens[creator], length);
  }

  /**
   * @notice see IMedia
   */
  function auctionTransfer(uint256 tokenId, address recipient) external override {
    require(msg.sender == marketContract, 'Media: only market contract');
    previousTokenOwners[tokenId] = ownerOf(tokenId);
    _safeTransfer(ownerOf(tokenId), recipient, tokenId, '');
  }

  /**
   * @notice see IMedia
   */
  function setAsk(uint256 tokenId, IMarket.Ask memory ask) public override nonReentrant onlyApprovedOrOwner(msg.sender, tokenId) {
    IMarket(marketContract).setAsk(tokenId, ask);
  }

  /**
   * @notice see IMedia
   */
  function removeAsk(uint256 tokenId) external override nonReentrant onlyApprovedOrOwner(msg.sender, tokenId) {
    IMarket(marketContract).removeAsk(tokenId);
  }

  /**
   * @notice see IMedia
   */
  function setBid(uint256 tokenId, IMarket.Bid memory bid) public override nonReentrant onlyExistingToken(tokenId) {
    require(msg.sender == bid.bidder, 'Market: Bidder must be msg sender');
    IMarket(marketContract).setBid(tokenId, bid, msg.sender);
  }

  /**
   * @notice see IMedia
   */
  function removeBid(uint256 tokenId) external override nonReentrant onlyTokenCreated(tokenId) {
    IMarket(marketContract).removeBid(tokenId, msg.sender);
  }

  /**
   * @notice see IMedia
   */
  function removeBidFromApp(uint256 tokenId, address sender) external override nonReentrant onlyTokenCreated(tokenId) onlyAuthorizedCaller {
    IMarket(marketContract).removeBid(tokenId, sender);
  }

  function setAskFromApp(uint256 tokenId, IMarket.Ask memory ask) public override nonReentrant onlyExistingToken(tokenId) onlyAuthorizedCaller {
    IMarket(marketContract).setAsk(tokenId, ask);
  }

  function setBidFromApp(uint256 tokenId, IMarket.Bid memory bid, address sender) external override nonReentrant onlyExistingToken(tokenId) onlyAuthorizedCaller {
    require(sender == bid.bidder, 'Market: Bidder must be msg sender');
    IMarket(marketContract).setBid(tokenId, bid, sender);
  }

  function setLazyBidFromApp(uint256 dropId, IDrop.Egg memory egg, IMarket.Bid memory bid, address sender) external override nonReentrant onlyAuthorizedCaller {
    require(sender == bid.bidder, 'Market: Bidder must be msg sender');
    IMarket(marketContract).setLazyBidFromApp(dropId, egg, bid, sender);
  }

  function removeLazyBidFromApp(uint256 dropId, string memory name, address sender) external override nonReentrant onlyAuthorizedCaller {
    IMarket(marketContract).removeLazyBidFromApp(dropId, name, sender);
  }

  function acceptLazyBidFromApp(uint256 dropId, IDrop.Egg memory egg, IZoo.Token memory token, IMarket.Bid memory bid) external override nonReentrant onlyAuthorizedCaller {
    IMarket(marketContract).acceptLazyBidFromApp(dropId, egg, token, bid);
  }

  /**
   * @notice see IMedia
   */
  function acceptBid(uint256 tokenId, IMarket.Bid memory bid) public override nonReentrant onlyApprovedOrOwner(msg.sender, tokenId) {
    IMarket(marketContract).acceptBid(tokenId, bid);
  }

  /**
   * @notice see IMedia
   */
  function acceptBidFromApp(uint256 tokenId, IMarket.Bid memory bid, address sender) external override nonReentrant onlyApprovedOrOwner(sender, tokenId) onlyAuthorizedCaller {
    IMarket(marketContract).acceptBid(tokenId, bid);
  }

  /**
   * @notice Burn a token.
   * @dev Only callable if the media owner is also the creator.
   */
  function burn(uint256 tokenId) public override nonReentrant onlyExistingToken(tokenId) onlyApprovedOrOwner(msg.sender, tokenId) {
    address owner = ownerOf(tokenId);

    require(tokenCreators[tokenId] == owner, 'Media: owner is not creator of media');

    _burn(tokenId);
  }

  /**
   * @notice Revoke the approvals for a token. The provided `approve` function is not sufficient
   * for this protocol, as it does not allow an approved address to revoke it's own approval.
   * In instances where a 3rd party is interacting on a user's behalf via `permit`, they should
   * revoke their approval once their task is complete as a best practice.
   */
  function revokeApproval(uint256 tokenId) external override nonReentrant {
    require(msg.sender == getApproved(tokenId), 'Media: caller not approved address');
    _approve(address(0), tokenId);
  }

  /**
   * @notice see IMedia
   * @dev only callable by approved or owner
   */
  function updateTokenURI(uint256 tokenId, string calldata _tokenURI)
    external
    override
    nonReentrant
    onlyApprovedOrOwner(msg.sender, tokenId)
    onlyTokenWithContentHash(tokenId)
    onlyValidURI(_tokenURI)
  {
    _setTokenURI(tokenId, _tokenURI);
    emit TokenURIUpdated(tokenId, msg.sender, _tokenURI);
  }

  /**
   * @notice see IMedia
   * @dev only callable by approved or owner
   */
  function updateTokenMetadataURI(uint256 tokenId, string calldata metadataURI)
    external
    override
    nonReentrant
    onlyApprovedOrOwner(msg.sender, tokenId)
    onlyTokenWithMetadataHash(tokenId)
    onlyValidURI(metadataURI)
  {
    _setTokenMetadataURI(tokenId, metadataURI);
    emit TokenMetadataURIUpdated(tokenId, msg.sender, metadataURI);
  }

  /**
   * @notice See IMedia
   * @dev This method is loosely based on the permit for ERC-20 tokens in  EIP-2612, but modified
   * for ERC-721.
   */
  function permit(
    address spender,
    uint256 tokenId,
    EIP712Signature memory sig
  ) public override nonReentrant onlyExistingToken(tokenId) {
    require(sig.deadline == 0 || sig.deadline >= block.timestamp, 'Media: Permit expired');
    require(spender != address(0), 'Media: spender cannot be 0x0');
    bytes32 domainSeparator = _calculateDomainSeparator();

    bytes32 digest = keccak256(
      abi.encodePacked('\x19\x01', domainSeparator, keccak256(abi.encode(PERMIT_TYPEHASH, spender, tokenId, permitNonces[ownerOf(tokenId)][tokenId]++, sig.deadline)))
    );

    address recoveredAddress = ecrecover(digest, sig.v, sig.r, sig.s);

    require(recoveredAddress != address(0) && ownerOf(tokenId) == recoveredAddress, 'Media: Signature invalid');

    _approve(spender, tokenId);
  }

  /* *****************
   * Private Functions
   * *****************
   */

  /**
   * @notice Creates a new token for `creator`. Its token ID will be automatically
   * assigned (and available on the emitted {IERC721-Transfer} event), and the token
   * URI autogenerated based on the base URI passed at construction.
   *
   * See {ERC721-_safeMint}.
   *
   * On mint, also set the sha256 hashes of the content and its metadata for integrity
   * checks, along with the initial URIs to point to the content and metadata. Attribute
   * the token ID to the creator, mark the content hash as used, and set the bid shares for
   * the media's market.
   *
   * Note that although the content hash must be unique for future mints to prevent duplicate media,
   * metadata has no such requirement.
   */
  function _mintForCreator(
    address creator,
    MediaData memory data,
    IMarket.BidShares memory bidShares
  ) internal onlyValidURI(data.tokenURI) onlyValidURI(data.metadataURI) {
    require(data.contentHash != 0, 'Media: content hash must be non-zero');
    // require(_contentHashes[data.contentHash] == false, 'Media: a token has already been created with this content hash');
    require(data.metadataHash != 0, 'Media: metadata hash must be non-zero');

    uint256 tokenId = _tokenIdTracker.current();

    _safeMint(creator, tokenId);
    _tokenIdTracker.increment();
    _setTokenContentHash(tokenId, data.contentHash);
    _setTokenMetadataHash(tokenId, data.metadataHash);
    _setTokenMetadataURI(tokenId, data.metadataURI);
    _setTokenURI(tokenId, data.tokenURI);
    _creatorTokens[creator].add(tokenId);
    _contentHashes[data.contentHash] = true;

    tokenCreators[tokenId] = creator;
    previousTokenOwners[tokenId] = creator;
    IMarket(marketContract).setBidShares(tokenId, bidShares);
  }

  function _setTokenContentHash(uint256 tokenId, bytes32 contentHash) internal virtual onlyExistingToken(tokenId) {
    tokenContentHashes[tokenId] = contentHash;
  }

  function _setTokenMetadataHash(uint256 tokenId, bytes32 metadataHash) internal virtual onlyExistingToken(tokenId) {
    tokenMetadataHashes[tokenId] = metadataHash;
  }

  function _setTokenMetadataURI(uint256 tokenId, string memory metadataURI) internal virtual onlyExistingToken(tokenId) {
    _tokenMetadataURIs[tokenId] = metadataURI;
  }

  /**
   * @notice Destroys `tokenId`.
   * @dev We modify the OZ _burn implementation to
   * maintain metadata and to remove the
   * previous token owner from the piece
   */
  function _burn(uint256 tokenId) internal override {
    string memory _tokenURI = _tokenURIs[tokenId];

    super._burn(tokenId);

    if (bytes(_tokenURI).length != 0) {
      _tokenURIs[tokenId] = _tokenURI;
    }

    delete previousTokenOwners[tokenId];
  }

  /**
   * @notice transfer a token and remove the ask for it.
   */
  function _transfer(
    address from,
    address to,
    uint256 tokenId
  ) internal override {
    IMarket(marketContract).removeAsk(tokenId);

    super._transfer(from, to, tokenId);
  }

  /**
   * @dev Calculates EIP712 DOMAIN_SEPARATOR based on the current contract and chain ID.
   */
  function _calculateDomainSeparator() internal view returns (bytes32) {
    uint256 chainId;
    /* solium-disable-next-line */
    assembly {
      chainId := chainid()
    }

    return
      keccak256(
        abi.encode(
          keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
          keccak256(bytes('ZOO')),
          keccak256(bytes('1')),
          chainId,
          address(this)
        )
      );
  }

  function _hashToken(address owner, IZoo.Token memory token) private view returns (IZoo.Token memory) {
    console.log('_hashToken', token.data.tokenURI, token.data.metadataURI);
    token.data.contentHash = keccak256(abi.encodePacked(token.data.tokenURI, block.number, owner));
    token.data.metadataHash = keccak256(abi.encodePacked(token.data.metadataURI, block.number, owner));
    return token;
  }

  function mintToken(address owner, IZoo.Token memory token) external override onlyAuthorizedCaller returns (IZoo.Token memory) {
    console.log('mintToken', owner, token.name, token.data.tokenURI);
    token = _hashToken(owner, token);
    _mintForCreator(owner, token.data, token.bidShares);
    uint256 id = getRecentToken(owner);
    token.id = id;
    return token;
  }

  function burnToken(address owner, uint256 tokenID) external override nonReentrant onlyExistingToken(tokenID) onlyApprovedOrOwner(owner, tokenID) {
    _burn(tokenID);
  }

  /**
   * @notice Helper to check that token has not been burned or minted
   */
  function tokenExists(uint256 tokenID) public view override returns (bool) {
    return _exists(tokenID);
  }

  function tokenCreator(uint256 tokenID) public view override returns (address) {
    return tokenCreators[tokenID];
  }

  function previousTokenOwner(uint256 tokenID) public view override returns (address) {
    return previousTokenOwners[tokenID];
  }

}

src/NFTStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./interfaces/I721Stake.sol";
import "./Owned.sol";

contract ZooNFTStaker is owned, I721Stake {
    using SafeMath for uint256;
	using SafeMath for uint40;
    bool stakeLive;
    uint256 public totalStakers;

    uint40 public minumTime;
    uint40 public mediumTime;
    uint40 public maximumTime;
    uint40 public timestampSeconds;

    struct Percentage {
        bool valid;
        uint256 amount;
    }

    mapping(uint256 => Percentage) public percentage;

    IERC20 public RewardCoin;

    mapping(address => Staker) public stakers;
    mapping (address => bool) public frozenStaker;
    mapping(address => mapping (uint => bool)) public frozenNft;

    function updateMinumTime(uint40 _new) public onlyOwner{
        minumTime = _new;
    }

    function updateMediumTime(uint40 _new) public onlyOwner{
        mediumTime = _new;
    }

    function updateTimeStampSeconds(uint40 _new) public onlyOwner{
        timestampSeconds = _new;
    }

    function updatemMaximumTime(uint40 _new) public onlyOwner{
        maximumTime = _new;
    }

    function updatePercentage(uint256 percentageLevel, uint256 amount) public onlyOwner{
        require(percentage[percentageLevel].valid, "percentage not active");
        percentage[percentageLevel].amount = amount;
    }

    function freezeStaker(address target, bool freeze) onlyOwner public {
        frozenStaker[target] = freeze;
    }

     function toggleStakeStatus() onlyOwner public {
        stakeLive = !stakeLive;
    }

    function freezeNft(address NftContract, uint256 _tokenId,  bool freeze) onlyOwner override public {
        frozenNft[NftContract][_tokenId] = freeze;
    }

    function isFrozenStaker(address _addr) public view  override returns (bool) {
        return frozenStaker[_addr];
    }

    function isFrozenNft(address NftContract, uint256 _tokenId) public override view returns (bool) {
        return frozenNft[NftContract][_tokenId];
    }


    constructor(address _rewardCoin) {
        require(isContract(_rewardCoin), "Reward coin not contract");
        RewardCoin = IERC20(_rewardCoin);
        minumTime = 30;
        mediumTime = 90;
        maximumTime = 180;
        timestampSeconds = 86400;
        percentage[1].valid = true;
        percentage[1].amount = 100;
        percentage[2].valid = true;
        percentage[2].amount = 200;
        percentage[3].valid = true;
        percentage[3].amount = 300;
    }

    modifier stakingModifier (address NftContract, uint256 _tokenId){
        require(!isFrozenStaker(msg.sender), "Caller Not allowed to stake");
        require(!isFrozenNft(NftContract, _tokenId), "NFT Not allowed to be staked");
        require(IERC721(NftContract).balanceOf(msg.sender) > 0, "Caller does't own the token");
        _;
    }

    function stake(address NftContract, uint256 _tokenId) stakingModifier(NftContract, _tokenId) override external {
        require(stakeLive, "Staking is not live");
        _stake(NftContract, _tokenId);
        emit NewStake(msg.sender, NftContract, _tokenId);
    }

    function _stake(address NftContract, uint256 _tokenId) internal {
        Staker storage staker = stakers[msg.sender];

        staker.tokens[NftContract][_tokenId].staked = true;

        staker.tokens[NftContract][_tokenId].period.push(StakingTime({
            time: uint40(block.timestamp)
        }));

        IERC721(NftContract).safeTransferFrom(msg.sender, address(this), _tokenId);

        emit NewStake(msg.sender, NftContract, _tokenId);
    }

    function unstake(address NftContract, uint256 _tokenId)  stakingModifier(NftContract, _tokenId) override public {
       _unstake(NftContract, _tokenId);
       emit unStaked(msg.sender, NftContract, _tokenId);
    } 

    function rewardAmount(address _addr, address NftContract, uint256 _tokenId) view external override returns(uint256[] memory) {
        Staker storage staker = stakers[_addr];

        uint256[] memory rewardValues = new uint256[](2);

        uint256 value = 0;
        uint256 totalDaysStaked = 0;

        for(uint256 i = 0; i < staker.tokens[NftContract][_tokenId].period.length; i++) {
            StakingTime storage StakingTimeInstance = staker.tokens[NftContract][_tokenId].period[i];
            uint daysStaked = (uint40(block.timestamp) - StakingTimeInstance.time) / timestampSeconds;
            if((daysStaked >= minumTime && daysStaked < mediumTime)){
                value += percentage[1].amount;
            }
            else if(daysStaked >= mediumTime && daysStaked < maximumTime){
                value += percentage[2].amount;
            }
            else if(daysStaked >= maximumTime){
                value += percentage[3].amount;
            }
            else{
                value += 0;
            }

            totalDaysStaked += daysStaked;

        }

        rewardValues[0] = value;
        rewardValues[1] = totalDaysStaked;

        return (rewardValues);
    }

    function _unstake(address NftContract, uint256 _tokenId) public {
       uint256[] memory rewardValues = this.rewardAmount(msg.sender, NftContract, _tokenId);
       require(rewardValues[1] > minumTime, "Not yet allowed to withdraw");
       IERC721(NftContract).safeTransferFrom(address(this), msg.sender, _tokenId);
       RewardCoin.transferFrom(owner, msg.sender, rewardValues[0]);
    }


    function updateRewardCoin(address _newRewardCoin) public override onlyOwner {
        RewardCoin = IERC20(_newRewardCoin);
    }    

    function isContract(address addr) internal view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

     function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external override returns (bytes4) {
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

}

src/Owned.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;


contract owned {
    address public owner;

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Caller should be Owner");
        _;
    }

    function transferOwnership(address newOwner) onlyOwner public {
        owner = newOwner;
    }
}

src/Random.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.4;

contract Random {
    uint8 public max = 100;

    constructor() {}

    struct Commit {
        bytes32 commit;
        uint64 block;
        bool revealed;
    }

    mapping(address => Commit) public commits;

    function commit(bytes32 dataHash) public {
        commits[msg.sender].commit = dataHash;
        commits[msg.sender].block = uint64(block.number);
        commits[msg.sender].revealed = false;
        emit CommitHash(
            msg.sender,
            commits[msg.sender].commit,
            commits[msg.sender].block
        );
    }

    event CommitHash(address sender, bytes32 dataHash, uint64 block);

    function reveal(bytes32 revealHash) public {
        //make sure it hasn't been revealed yet and set it to revealed
        require(
            commits[msg.sender].revealed == false,
            "CommitReveal::reveal: Already revealed"
        );
        commits[msg.sender].revealed = true;
        //require that they can produce the committed hash
        require(
            getHash(revealHash) == commits[msg.sender].commit,
            "CommitReveal::reveal: Revealed hash does not match commit"
        );
        //require that the block number is greater than the original block
        require(
            uint64(block.number) > commits[msg.sender].block,
            "CommitReveal::reveal: Reveal and commit happened on the same block"
        );
        //require that no more than 250 blocks have passed
        require(
            uint64(block.number) <= commits[msg.sender].block + 250,
            "CommitReveal::reveal: Revealed too late"
        );
        //get the hash of the block that happened after they committed
        bytes32 blockHash = blockhash(commits[msg.sender].block);
        //hash that with their reveal that so miner shouldn't know and mod it with some max number you want
        // uint256 random = uint256(
        //     keccak256(abi.encodePacked(blockHash, revealHash))
        // ) % max;

        bytes32 bytesHash = keccak256(abi.encodePacked(blockHash, revealHash));

        uint256 bytesHashToUint = uint256(bytesHash);

        uint256 random = bytesHashToUint % max;

        emit RevealHash(msg.sender, revealHash, uint8(random));
    }

    event RevealHash(address sender, bytes32 revealHash, uint8 random);

    function getHash(bytes32 data) public view returns (bytes32) {
        return keccak256(abi.encodePacked(address(this), data));
    }

    function revealAnswer(bytes32 answer, bytes32 salt) public {
        //make sure it hasn't been revealed yet and set it to revealed
        require(
            commits[msg.sender].revealed == false,
            "CommitReveal::revealAnswer: Already revealed"
        );
        commits[msg.sender].revealed = true;
        //require that they can produce the committed hash
        require(
            getSaltedHash(answer, salt) == commits[msg.sender].commit,
            "CommitReveal::revealAnswer: Revealed hash does not match commit"
        );
        emit RevealAnswer(msg.sender, answer, salt);
    }

    event RevealAnswer(address sender, bytes32 answer, bytes32 salt);

    function getSaltedHash(bytes32 data, bytes32 salt)
        public
        view
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(address(this), data, salt));
    }
}

